mapper.delete:
delete from area_info
where
area_code = #{areaCode}

  AND company_id = #{companyId}

delete from area_info
where
area_id = #{areaId}

    AND company_id = #{companyId}

mapper.select:
select area.area_id, area.ware_code, ware.company_id, area.area_code, area.area_name, area.add_time, area.memo, area.abc_class, ware.ware_name from area_info area left join ware_info ware on area.ware_code = ware.ware_code left join company com on ware.company_id = com.company_id where 1=1
AND ware.ware_name LIKE CONCAT('%', #{keyWords}, '%') OR area.area_name LIKE CONCAT('%', #{keyWords}, '%')
AND area.ware_code =#{wareCode}
AND area.areaCode =#{areaCode}
AND com.company_id =#{companyId}

select area.area_id, area.ware_code, area.company_id, area.area_code, area.area_name, area.add_time, area.memo, area.abc_class, ware.ware_name from area_info area left join ware_info ware on area.ware_code = ware.ware_code and area.company_id = ware.company_id
AND (area.memo LIKE CONCAT('%', #{keywords}, '%') OR area.area_name LIKE CONCAT('%', #{keywords}, '%') or area.area_code LIKE CONCAT('%', #{keywords}, '%') )
AND area.ware_code = #{wareCode}
AND area.company_id =#{companyId}
order by add_time, area_code

select count(*) from area_info area
inner join shelf_info shelf on area.area_code = shelf.area_code and area.company_id = shelf.company_id


        AND area.area_id = #{areaId}


        AND area.company_id = #{companyId}
