mapper.delete:
delete from item_type
  where item_type_code = #{itemTypeCode}

      AND company_id = #{companyId}

delete from item_type
where item_type_id = #{itemTypeId}

    AND company_id = #{companyId}

mapper.select:
select count(*) from item_type itemType inner join mt_alone_product product on itemType.item_type_code = product.product_type_code and itemType.company_id = product.company_id
product.state = 'normal'
AND itemType.item_type_id = #{itemTypeId}
AND itemType.company_id = #{companyId}

select   count(*)
from item_type itemType


        AND itemType.parent_id = #{itemTypeId}


        AND itemType.company_id = #{companyId}

select * from item_type itemType


        and itemType.parent_id = #{itemTypeId}


        AND itemType.company_id = #{companyId}

 ORDER BY itemType.item_type_id, itemType.item_type_code

select * from item_type itemType


        and itemType.item_type_id = #{parentId}


        AND itemType.company_id = #{companyId}
