mapper.delete:
delete from shelf_info
where shelf_id = #{shelfId}


    AND company_id=#{companyId}

mapper.select:
select count(*)
from mt_alone_product product where product.state='normal'


      AND substring_index(substring_index(product.cell_code,'-',-3),'-',1)=#{shelfId}


      AND product.company_id=#{companyId}

select shelf.shelf_id, shelf.area_code, shelf.shelf_code, shelf.shelf_name, shelf.a_column,
shelf.a_row, shelf.add_time, shelf.memo, shelf.abc_class, area.area_name,
ware.ware_name, ware.ware_code, shelf.shelf_row, shelf.shelf_column, shelf.order_no
from shelf_info shelf left join area_info area on shelf.area_code = area.area_code left join ware_info ware on area.ware_code = ware.ware_code left join company com on ware.company_id = com.company_id where 1=1
AND area.area_name LIKE CONCAT('%', #{keyWords}, '%') OR ware.ware_name LIKE CONCAT('%', #{keyWords}, '%') OR shelf.shelf_name LIKE CONCAT('%', #{keyWords}, '%')
AND area.area_code=#{areaCode}
AND ware.ware_code=#{wareCode}
AND com.company_id=#{companyId}

select * from shelf_info shelf left join area_info area on shelf.area_code = area.area_code and shelf.company_id = area.company_id left join ware_info ware on area.ware_code = ware.ware_code and area.company_id = ware.company_id
AND (shelf.shelf_name LIKE CONCAT('%', #{keywords}, '%') OR shelf.shelf_code LIKE CONCAT('%', #{keywords}, '%') OR shelf.shelf_row LIKE CONCAT('%', #{keywords}, '%') OR shelf.shelf_column LIKE CONCAT('%', #{keywords}, '%') OR area.area_name LIKE CONCAT('%', #{keywords}, '%') OR ware.ware_name LIKE CONCAT('%', #{keywords}, '%') OR shelf.shelf_name LIKE CONCAT('%', #{keywords}, '%') )
AND shelf.area_code =#{areaCode}
AND area.ware_code=#{wareCode}
AND shelf.company_id=#{companyId}
order by shelf.area_code,shelf.shelf_code,shelf.add_time
