mapper.delete:
delete from supplier_manage

where  supplier_code = #{supplierCode}


  AND company_id = #{companyId}

delete from supplier_manage

where  supplier_id = #{supplierId}

    AND company_id = #{companyId}

mapper.select:
select count(*) from supplier_manage supplier inner join mt_alone_product product on supplier.supplier_code = product.supplier_code and supplier.company_id = product.company_id
state = 'normal'
AND supplier_id = #{supplierId}
AND supplier.company_id = #{companyId}
