mapper.delete:
delete
from bill_detail
where
id = #{id}

  AND company_id = #{companyId}

mapper.select:
select detail.id, detail.detail_no, detail.bill_no, detail.ware_code, detail.area_code , detail.cell_code , detail.to_ware_code , detail.to_area_code , detail.to_cell_code , detail.item_code , detail.quantity, detail.memo , detail.state , detail.add_time , detail.task_time , detail.bill_type , detail.accept_quantity ,
detail.complete_quantity , detail.check_memo , detail.company_id , detail.batch, detail.need_return, ware.ware_name , area.area_name , item.item_name, item.item_class, u.unit_name, toware.ware_name as to_ware_name, toarea.area_name as to_area_name
from bill_detail detail
left join area_info area on detail.area_code = area.area_code left join ware_info toware on detail.to_ware_code = toware.ware_code left join area_info toarea on detail.to_area_code = toarea.area_code left join item_info item on detail.item_code = item.item_code left join unit u on item.unit_code = u.unit_code left join bill_master master on detail.bill_no = master.bill_no left join ware_info ware on master.ware_code = ware.ware_code where 1=1
and master.bill_no = #{billNo}
and master.ware_code = #{wareCode}
