mapper.select:
select master.id, master.bill_no, master.type, master.add_time, master.add_user_id, master.check_time, master.check_user_id, master.task_time, master.task_user_id, master.state, master.ware_code, master.company_id, master.memo, master.batch, master.contract_no, master.item_master_id, master.in_type, com.company_name, ware.ware_name, im.item_master_name, (select user_name from user_info where user_id = master.add_user_id) as add_user_name, (select user_name from user_info where user_id = master.check_user_id) as check_user_name, (select user_name from user_info where user_id = master.task_user_id) as task_user_name, (select user_name from user_info where user_id = master.accept_user_id) as accept_user_name
from bill_master master left join ware_info ware on master.ware_code = ware.ware_code left join company com on master.company_id = com.company_id left join item_master im on master.item_master_id = im.item_master_id where 1=1
and master.bill_no like concat('%',#{keyWords}'%')
and com.company_id = #{companyId}
and master.add_time > = #{workStartTime}
and master.add_time < = #{workEndTime}
and master.ware_code = #{wareCode}
and master.item_master_id = #{itemMasterId}
and master.type = #{type}
and master.state = #{state}
and master.bill_no like CONCAT('%', #{billNo}, '%')
order by master.add_time desc ,master.state

SELECT
(
SELECT COUNT(*) FROM bill_master WHERE type = 1

 and  ware_code = #{wareCode}


and  item_master_id = #{itemMasterId}


 and company_id =#{companyId}

) AS intotal1,
(
SELECT COUNT(*) FROM bill_master WHERE type = 1 and state =1

  and  ware_code = #{wareCode}


  and  item_master_id = #{itemMasterId}


  and company_id =#{companyId}

) AS intotal2,
(
SELECT COUNT(*) FROM bill_master WHERE type = 1 and state =2

  and  ware_code = #{wareCode}


  and  item_master_id = #{itemMasterId}


  and company_id =#{companyId}

) AS intotal3,
(
SELECT COUNT(*) FROM bill_master WHERE type = 1 and state =3

  and  ware_code = #{wareCode}


  and  item_master_id = #{itemMasterId}


  and company_id =#{companyId}

) AS intotal4,
(
SELECT COUNT(*) FROM bill_master WHERE type = 2

  and  ware_code = #{wareCode}


  and  item_master_id = #{itemMasterId}


  and company_id =#{companyId}

) AS outtotal1,
(
SELECT COUNT(*) FROM bill_master WHERE type = 2 and state =3

  and  ware_code = #{wareCode}


  and  item_master_id = #{itemMasterId}


  and company_id =#{companyId}

) AS outtotal2,
(
SELECT COUNT(*) FROM bill_master WHERE type = 2 and state =4

  and  ware_code = #{wareCode}


  and  item_master_id = #{itemMasterId}


  and company_id =#{companyId}

) AS outtotal3,
(
 SELECT COUNT(*) FROM bill_master WHERE type = 2 and state =5

  and  ware_code = #{wareCode}


  and  item_master_id = #{itemMasterId}


  and company_id =#{companyId}

) AS outtotal4,



TRUNCATE((
SELECT COUNT(*) FROM cell_info cell
left join  shelf_info shelf on cell.shelf_code = shelf.shelf_code
left join  area_info area on shelf.area_code = area.area_code
left join ware_info ware on area.ware_code = ware.ware_code
left join  company com on ware.company_id = com.company_id
WHERE   cell.state =1

  and  ware.ware_code = #{wareCode}



  and com.company_id =#{companyId}

) / (SELECT COUNT(*) FROM cell_info cell
left join  shelf_info shelf on cell.shelf_code = shelf.shelf_code
left join  area_info area on shelf.area_code = area.area_code
left join ware_info ware on area.ware_code = ware.ware_code
left join  company com on ware.company_id = com.company_id
WHERE   1 =1

  and  ware.ware_code = #{wareCode}



  and com.company_id =#{companyId}

)  ,2) as cellDouble

FROM DUAL;
